/* import React, { Fragment, Component } from 'react'; */
import { Helmet } from 'react-helmet';
import React, {Fragment,} from 'react';  {  }
import { Link } from 'react-router-dom';

class Register extends Component {
    constructor() {
        super();

        this.state = {
            matric: '',
            password: '',
            username: '',
            hasAgreed: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;

        this.setState({
          [name]: value
        });
    }

    handleSubmit(e) {
        e.preventDefault();

        console.log('The form was submitted with the following data:');
        console.log(this.state);
    }

    render() {
        return (
    <Fragment>   
        <Helmet><title>Nimelssa Quiz - Registeration page</title></Helmet>
        <div className="FormCenter">
            <form onSubmit={this.handleSubmit} className="FormFields">

              <div className="FormField">
                <label className="FormField__Label" htmlFor="name">Full Name</label>
                <input 
                type="text" 
                id="name" 
                className="FormField__Input" 
                placeholder="Enter your full name" 
                name="name" 
                value={this.state.username} 
                onChange={this.handleChange} />
              </div>

              <div className="FormField">
                <label className="FormField__Label" htmlFor="matric">Matric Number</label>
                <input 
                type="number" 
                id="matric" 
                className="FormField__Input" 
                placeholder="Enter your matric" 
                name="matric" 
                value={this.state.matric} 
                onChange={this.handleChange} />
              </div>

              <div className="FormField">
                <label className="FormField__Label" htmlFor="password">Password</label>
                <input 
                type="password" 
                id="password" 
                className="FormField__Input" 
                placeholder="Enter your password" 
                name="password" 
                value={this.state.password} 
                onChange={this.handleChange} />
              </div>

              <div className="FormField">
                <label className="FormField__CheckboxLabel">
                    <input 
                    className="FormField__Checkbox" 
                    type="checkbox" 
                    name="hasAgreed" 
                    value={this.state.hasAgreed} 
                    onChange={this.handleChange} /> I agree all statements in <a href="" className="FormField__TermsLink">terms of service</a>
                </label>
              </div>

              <div className="FormField">
                  <button className="FormField__Button mr-20">Sign Up</button> <Link to="/sign-in" className="FormField__Link">I'm already member</Link>
              </div>
            </form>
          </div>
        </Fragment>      
        );
    }
}

export default Register;


