import React, { Fragment, useState,useRef,useEffect } from 'react';
import AuthService from '../Services/AuthService';
import Message from '../components/Message';
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';


const Register = props=>{
  const [user,setUser] = useState({userName: "", matric: "", password: "", confirmedPassword: "", role: ""});
  const [message,setMessage] = useState(null);
  let timerID = useRef(null);
 
  useEffect(()=>{
       return ()=>{
         clearTimeout(timerID);
       }
  },[]);

  const onChange = e =>{
    setUser({...user,[e.target.name] : e.target.value});
  }

  const resetForm = ()=>{
     setUser({userName: "", matric : "", password : "",confirmedPassword: "", role : ""})
  }

  const onSubmit = e =>{
    e.preventDefault()
    AuthService.register(user).then(data=>{
      const { message } = data;
      setMessage(message);
      resetForm();
      if(!message.msgError){
        timerID = setTimeout(()=>{
           props.history.push('/user/login')
        },2000  )
      }
     
    })
  }
   
      return(
          <Fragment>
              <Helmet><title>Nimelssa Quiz - Registeation page</title></Helmet>
              <div className='row'>
    <form className="col s12" noValidate  onSubmit={onSubmit}>
       <div className="row">
       <div className="col s12 m4 offset-m4">
            <div className="card z-depth-4">
              <div className="card-content black white-text">
                 <span className="card-title">Create Account</span>
              </div>
              <div className="card-content  white dark-3">

                      <div className="row">
                      <div className="input-field">
                        <i className="material-icons prefix">account_circle</i>
                        <input 
                        placeholder="Enter Full Name"
                        id="userName" 
                        value={user.userName}
                        type="text" 
                        required="required" 
                        name="userName" 
                        noValidate 
                        onChange={onChange}
                        >

                        </input>
                        <label htmlFor="userName" className="active">User Name</label>
                      </div>
                      </div>

                      <div className="row">
                      <div className="input-field">
                          <i className="material-icons prefix">edit</i>
                          <input 
                            placeholder="Enter Matric"
                            value={user.matric}
                            id="matric" 
                            type="number"
                            required="required"
                            name="matric"
                            noValidate 
                            onChange={onChange}
                            >
                              
                            </input>
                          <label htmlFor="matric" className="active">Matric NUmber</label>
                      </div>

                        <div className="inputDiv"><b>Hover to view password Instruction.</b>
                            <ul className="input-requirement">
                              <li>At least 8 characters long(and not more than than 12 characters)</li>
                              <li>At least 1 numeric character</li>
                              <li>At least 1 Lower Case alphabetical character(a-z)</li>
                              <li>At least 1 Upper Case alphabetical character(A-Z)</li>
                              <li>At least 1 non-alphanumeric symbol(e.g. @&%)</li>
                            </ul>
					                </div>
                        </div>

                          <div className="row">
                          <div className="input-field">
                            <i className="material-icons prefix">lock</i>
                            <input  
                            placeholder="Enter Password" 
                            value={user.password}
                            id="password" 
                            type="password" 
                            required="required" 
                            name="password" 
                            noValidate 
                            onChange={onChange}
                            >
                            
                            </input>
                            <label htmlFor="password" className="active">Password</label>
                          </div>
                          </div>

                 <div className="row">
                 <div className="input-field">
                    <i className="material-icons prefix">lock</i>
                    <input 
                    id="pass" 
                    value={user.confirmedPassword}
                    type="password" 
                    required="required" 
                    placeholder="Confirm Password" 
                    name="confirmPassword" 
                    noValidate 
                    onChange={onChange}
                    >
                  
                  </input>
                    <label htmlFor="pass" className="active">Confirm Password</label>
                  </div>
                 </div>

                 <div className="row">
                 <div className="input-field">
                    <i className="material-icons prefix">lock</i>
                    <input 
                    id="pass" 
                    type="text" 
                    value={user.role}
                    required="required" 
                    placeholder="Enter role(Admin/User)" 
                    name="role" 
                    noValidate 
                    onChange={onChange}
                    >
                  
                  </input>
                    <label htmlFor="role" className="active">Role</label>
                  </div>
                 </div>

                    <div>
                      <label>
                      &nbsp;<button className="waves-effect waves-light btn purple button" style={{width:"100%"}} type="submit">Register</button>
                       {/*  &nbsp;<button className="waves-effect waves-light btn purple button" style={{width:"100%"}}><Link to="/user/login">Register</Link></button> */}
                      </label>
                    </div>
                    <h6>Already hava an account? <Link to="/user/login">Login</Link></h6>
              </div>
            </div>
         </div>
       </div>
    </form>
    {message ? <Message message={message}/> : null}
</div> 
          </Fragment>
      )}
  export default Register;
