import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Home from './components/Home';
import QuizInstructions from './components/quiz/Quizinstructions';
import Play from './components/quiz/play';
import QuizSummary from './components/quiz/QuizSummary';
import Register from './components/Register';
import Login from './components/Login';

const fakeAuth = {
  isAuthenticated: false,
  authenticate (cb){
    this.isAuthenticated = true
    setTimeout(cb, 100);
  },
  signOut(cb){
    this.isAuthenticated = false
    setTimeout(cb, 100);
  }
}

 const ProtectedRoute = ({ component:Component, ...rest}) =>{
    return <Route {...rest} render={(props)=>{
       return fakeAuth.isAuthenticated === tru ? <Component {...props} /> : <Redirect to="/user/play/login" />
    }} />
 } 

class App extends React.Component {
  render(){
    return ( 
      <Router>
        <Switch>
             <Route exact path="/" component={Home} />
             <ProtectedRoute path="/user/play/instructions" exact component={QuizInstructions} />
             <ProtectedRoute path="/user/play/quiz" exact component={Play} /> 
             <ProtectedRoute path="/user/play/quizSummary" exact component={QuizSummary} />
             <Route path="/user/play/register" exact component={Register}/>
             <Route path="/user/play/login" exact component={Login}/>
              <Route exact component={NotFound}/> 
      </Switch>      
      </Router>
    )
  }
}

function NotFound() {
  return(
    <div>
      <h1>Page Not Found</h1>
    </div>
  )
}

export default App;
