/* import React, { Fragment, Component } from 'react'; */
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';
import React, {Fragment} from 'react';



 
const Login = props=>{
  const [user,setUser] = useState({matric: "", password: ""});
  const [message,setMessage] = useState(null);
  const authContext = useContext(AuthContext);

const onChange = e =>{
  setUser({...user,[e.target.name] : e.target.value});
}

const onSubmit = e =>{
  e.preventDefault()
  AuthService.login(user).then(data=>{
    const { isAuthenticated,user,message} = data;
    if(isAuthenticated){
       authContext.setUser(user);
       authContext.setIsAuthenticated(isAuthenticated);
       props.history.push('/play/instructions');
     }
     else 
       setMessage(message);
  })
}
    return(
      <Fragment>
      <Helmet><title>Nimelssa Quiz - Login page</title></Helmet>
      <div className='row'>
<form className="col s12" autoComplete="off" onSubmit={onSubmit}>
 <div className="row">
 <div className="col s12 m4 offset-m4">
      <div className="card z-depth-4">
        <div className="card-content black white-text">
           <span className="card-title">Please Login</span>
        </div>
        <div className="card-content  white dark-3">
          
           <div className="row">
           <div className="input-field">
              <i className="material-icons prefix">edit</i>
              <input  
              name="matric"
              onChange={onChange}
              placeholder="Enter Matric" 
              id="matric" 
              type="number" 
              className="validate" 
              required="required"></input>
              <label htmlFor="matric" className="active">Matric NUmber</label>
            </div>
           </div>

            <div className="row">
            <div className="input-field">
              <i className="material-icons prefix">lock</i>
              <input  
              name="password"
              onChange={onChange}
              placeholder="Enter Password" 
              id="pass" 
              type="password" 
              className="validate" 
              required="required"></input>
              <label htmlFor="password" className="active">Password</label>
            </div>
            </div>

              <div>
                <label>
                  &nbsp;<button className="waves-effect waves-light btn purple  button" style={{width:"100%"}} type="submit">Login</button>{/* 
                  &nbsp;<button className="waves-effect waves-light btn purple  button" style={{width:"100%"}}><Link to="/play/quiz">Login</Link></button> */}
                </label>
              </div>
              <h6>Dont have an account? <Link to="/user/register">Register</Link></h6>
        </div>
      </div>
   </div>
 </div>
</form>
{message ? <Message message={message}/> : null}
</div> 
  </Fragment> 
    )}
export default Login;
