import React, {
  Fragment,
  Component
} from 'react';
import {
  Helmet
} from 'react-helmet';
import {
  Link
} from 'react-router-dom';
/* import { Link } from 'react-router-dom'; */
/* import { Button } from 'materialize-css'; */

class Register extends Component {
  constructor(props) {
    super(props)

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeMatric = this.onChangeMatric.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeConfirmPassword = this.onChangeConfirmPassword.bind(this);

    this.state = {
      name: "",
      matric: "",
      password: "",
      confirmPassword: "",
      formErrors: {
        name: "",
        matric: "",
        password: "",
        confirmPassword: "",
      }
    };
  }

  onChangeName = (event) => {
    this.setState({
      name: event.target.value
    });
  }

  onChangeMatric = (event) => {
    this.setState({
      matric: event.target.value
    });
  }

  onChangePassword = (event) => {
    this.setState({
      password: event.target.value
    });
  }

  onChangeConfirmPassword = (event) => {
    this.setState({
      confirmPassword: event.target.value
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    let newUser = {
      name: this.state.name,
      matric: this.state.matric,
      password: this.state.password,
      confirmPassword: this.state.confirmPassword
    }
    console.log(newUser);
    window.location = '/user/login';
  }
  render() {
    return ( <
      Fragment >
      <
      Helmet > < title > Nimelssa Quiz - Registeation page < /title></Helmet >
      <
      div className = 'row' >
      <
      form className = "col s12"
      onSubmit = {
        this.handleSubmit
      }
      noValidate >
      <
      div className = "row" >
      <
      div className = "col s12 m4 offset-m4" >
      <
      div className = "card z-depth-4" >
      <
      div className = "card-content black white-text" >
      <
      span className = "card-title" > Create Account < /span> <
      /div> <
      div className = "card-content  white dark-3" >

      <
      div className = "row" >
      <
      div className = "input-field" >
      <
      i className = "material-icons prefix" > account_circle < /i> <
      input placeholder = "Enter Full Name"
      id = "name"
      type = "text"
      required = "required"
      name = "name"
      noValidate onChange = {
        this.onChangeName
      }
      value = {
        this.state.name
      } > < /input> <
      label
      for = "name"
      className = "active" > User Name < /label> <
      /div> <
      /div>

      <
      div className = "row" >
      <
      div className = "input-field" >
      <
      i className = "material-icons prefix" > edit < /i> <
      input placeholder = "Enter Matric"
      id = "matric"
      type = "number"
      required = "required"
      name = "matric"
      noValidate onChange = {
        this.onChangeMatric
      }
      value = {
        this.state.matric
      } > < /input> <
      label
      for = "matric"
      className = "active" > Matric NUmber < /label> <
      /div>

      <
      div className = "inputDiv" > < b > Hover to view password Instruction. < /b> <
      ul className = "input-requirement" >
      <
      li > At least 8 characters long(and not more than than 12 characters) < /li> <
      li > At least 1 numeric character < /li> <
      li > At least 1 Lower Case alphabetical character(a - z) < /li> <
      li > At least 1 Upper Case alphabetical character(A - Z) < /li> <
      li > At least 1 non - alphanumeric symbol(e.g.@ & % ) < /li> <
      /ul> <
      /div> <
      /div>

      <
      div className = "row" >
      <
      div className = "input-field" >
      <
      i className = "material-icons prefix" > lock < /i> <
      input placeholder = "Enter Password"
      id = "password"
      type = "password"
      required = "required"
      name = "password"
      noValidate onChange = {
        this.onChangePassword
      }
      value = {
        this.state.password
      } > < /input> <
      label
      for = "password"
      className = "active" > Password < /label> <
      /div> <
      /div>

      <
      div className = "row" >
      <
      div className = "input-field" >
      <
      i className = "material-icons prefix" > lock < /i> <
      input id = "pass"
      type = "password"
      required = "required"
      placeholder = "Confirm Password"
      name = "confirmPassword"
      noValidate onChange = {
        this.onChangeConfirmPassword
      }
      value = {
        this.state.confirmPassword
      } > < /input> <
      label
      for = "pass"
      className = "active" > Confirm Password < /label> <
      /div> <
      /div>

      <
      div >
      <
      label >
      &
      nbsp; < button className = "waves-effect waves-light btn purple button"
      style = {
        {
          width: "100%"
        }
      } > < Link to = "/user/login" > Register < /Link></button >
      <
      /label> <
      /div> <
      h6 > Already hava an account ? < Link to = "/user/login" > Login < /Link></h6 >
      <
      /div> <
      /div> <
      /div> <
      /div> <
      /form> <
      /div>  <
      /Fragment>
    )
  }
}
export default Register;
