 import { Helmet } from 'react-helmet';
import React, { Component, Fragment } from 'react';

class Register extends Component{

  state = {
      username: "",
      usernameError: "",
      matric: "",
      matricError: "",
      password: "",
      passwordError: "",
      role: "",
      roleError: ""
    }

handleChange = (event) => {
 this.setState({
    [event.target.name] : event.target.value
  });
};

validateForErrors = () =>{
  let isError = false;
  const errors = {};

  if (this.state.username.length < 8){
    isError = true;
    errors.usernameError="Username needs to be at least 8 characters long"
  }

  if (isError){
    this.setState({
      ...this.state,
      ...errors
    })
  }

 return isError;
}

handleSubmit = (event) =>{
event.preventDefault();

const error = this.validateForErrors();
if(!error){
//Empty the form after submitting
this.setState({
  username: "",
  matric: "",
  password: "",
  role: "",
})

this.props.onChange({
  username: "",
  matric: "",
  password: "",
  role: "",
  });
  }
}

