 import { Helmet } from 'react-helmet';
import React, { Component, Fragment } from 'react';

class Register extends Component{

  state = {
      username: "",
      usernameError: "",
      matric: "",
      matricError: "",
      password: "",
      passwordError: "",
      role: "",
      roleError: ""
    }

handleChange = (event) => {
 this.setState({
    [event.target.name] : event.target.value
  });
};

validateForErrors = () =>{
  let isError = false;
  const errors = {};

  if (this.state.username.length < 8){
    isError = true;
    errors.usernameError="Username needs to be at least 8 characters long"
  }

  if (isError){
    this.setState({
      ...this.state,
      ...errors
    })
  }

 return isError;
}

handleSubmit = (event) =>{
event.preventDefault();

const error = this.validateForErrors();
if(!error){
//Empty the form after submitting
this.setState({
  username: "",
  matric: "",
  password: "",
  role: "",
})

this.props.onChange({
  username: "",
  matric: "",
  password: "",
  role: "",
  });
  }
}

  render(){
    return(
<Fragment>
      <Helmet><title>Nimelssa quiz - Registeration page</title></Helmet>
    <div className="row">
      <div className="col s12 m4 offset-m4"> 
         <div className="card z-depth-4">
            <div className="card-content black white-text">
               <span className="card-title">Craete Account</span>
            </div>
            <div className="card-content">

               <div className="input-field">
                 <i className="material-icons prefix">account_box</i>
                 <input
                 className="validate"
                 placeholder="Enter Full Name"
                  id="uid"
                  type="text"
                  name="username"
                  value={this.state.username}
                  onChange={ event=> this.handleChange(event)}
                  data-error={this.state.usernameError}
                   />
                 <label 
                 htmlFor="uid"
                 data-error={this.state.usernameError}
                 >User Name</label>
               </div>

               <div className="input-field">
                 <i className="material-icons prefix">edit</i>
                 <input 
                 className="validate"
                  placeholder="Enter Matric Number"
                 id="matric" 
                 type="number"
                 name="number"
           /*      value={this.state.matric} */
                onChange={(event)=>{this.handleChange(event)}}
                data-error={this.state.matricError}
                  />
                 <label htmlFor="matric">Matric Number</label>
               </div>
              
               <div className="input-field">
                 <i className="material-icons prefix">lock</i>
                 <input 
                 className="validate"
                  placeholder="Enter Password"
                 id="password" 
                 type="password"
                 name="password"
                 value={this.state.password}
                 onChange={(event)=>{this.handleChange(event)}}
                 data-error={this.state.passwordError}
                  />
                 <label htmlFor="password">Password</label>
               </div>

               <div className="input-field">
                 <i className="material-icons prefix">account_circle</i>
                 <input 
                 className="validate"
                  placeholder="Enter Role(admin/user)"
                 id="role" 
                 type="text"
                 name="role"
                 value={this.state.role}
                 onChange={(event)=>{this.handleChange(event)}}
                 data-error={this.state.roleError}
                 />
                 <label htmlFor="role">Role</label>
               </div>
               
               <div >
                 <label>
                  &nbsp;<input id="indeterminate-checkbox" type="checkbox" />
                  <span>Do you approve the above deatils</span>
                 </label>
               </div><br />

               <div >
                 <label>
                  &nbsp;<button style={{width:100}} className="waves-effect waves-light btn blue" onClick={(event)=>{this.handleSubmit(event)}}>Register</button>
                 </label>
               </div><br />

            </div>
         </div>
      </div>
    </div>
</Fragment>
    )
  }
}
export default Register;
